use std::collections::HashSet;

fn find_n_unique_sequence(n: usize, input: &str) -> usize {
    let own_input = input.to_owned();
    let mut counter = n;

    for seq in own_input
        .chars()
        .collect::<Vec<char>>()
        .windows(n.try_into().unwrap())
    {
        let set: HashSet<&char> = HashSet::from_iter(seq);
        if set.len() == n {
            return counter;
        }
        counter += 1;
    }

    return usize::MAX;
}

fn main() {
    // let input = String::from("bvwbjplbgvbhsrlpgdmjqwftvncz");
    let input = String::from("");
    println!("Part 1: {}", find_n_unique_sequence(4, &input));
    println!("Part 2: {}", find_n_unique_sequence(14, &input));
}
